import React, { Component } from 'react';


import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

import './Contact.css';
import axios from 'axios';

export default class Example extends React.Component {
  
 /* constructor(props) {
  /*super(props);
        this.onnameChange = this.onnameChange.bind(this);
        this.onbirthChange = this.onbirthChange.bind(this);
        this.onfollowersChange = this.onfollowersChange.bind(this);
        this.onAlbumChange = this.onAlbumChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);*/

  state = {
    name :'',
    birth:'',
    followers:'',
   // Album:'',
  }

  handleChange = event => {
    this.setState({ name: event.target.value });
  }
  handleChange1 = event => {
    this.setState({ birth: event.target.value });
  }
  handleChange2 = event => {
    this.setState({ followers: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();

    var artists = {
      name: this.state.name,
      birth: this.state.birth,
      followers: this.state.followers
    };

    axios.put('http://localhost:3001/artists', artists )
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
    }

    

//}

