  render() {
    return (

      <div className="App-header" >
      <div className="row">
      <div className="col-4">
    
     
      <Form onSubmit={this.handleSubmit}>

      <h1>Artiste</h1>

      
        <FormGroup>
          <Label for="nom">Nom</Label>
          <Input id="NomArtiste" onChange={this.handleChange}  />
        </FormGroup>
        <FormGroup>
          <Label for="Date">Birth</Label>
          <Input
            type="date"
            id="DatedeNaissance"
            onChange={this.handleChange1}
            
          />
        </FormGroup>
        <FormGroup>
          <Label for="Followers">Followers</Label>
          <Input
            type="number"
            id="nbrfollowers"
            onChange={this.handleChange2}
           
          />
        </FormGroup>
       
        <FormGroup>
          <Label for="Albums">Albums</Label>
          <Input type="textarea" name="album" id="albums" />
        </FormGroup>
        <Button className="Submit">Submit</Button>
      </Form>

      </div>

      <div className="col-4">
      <Form onSubmit={this.handleSubmit1}>
      <h1>Album</h1>
      
        <FormGroup>
          <Label for="titre">Titre</Label>
          <Input type="text" name="Titre" id="TitreAlbum"  />
        </FormGroup>
        <FormGroup>
          <Label for="Date">Date de sortie</Label>
          <Input
            id="DatedeSortie"
          />
        </FormGroup>

        <FormGroup>
          <Label for="genre">Genre</Label>
          <Input type="text" name="Genre" id="GenreAlbum"  />
        </FormGroup>
        <FormGroup>
          <Label for="urlcover">URL Cover</Label>
          <Input type="text" name="UrlCover" id="UrlCoverAlbum"  />
        </FormGroup>
        <FormGroup>
          <Label for="reftitres">Ref Titres</Label>
          <Input type="text" name="RefTitres" id="RefTitresAlbum"  />
        </FormGroup>

        <Button className="Submit">Submit</Button>
      </Form>
      </div>


      <div className="col-4">
      <Form onSubmit={this.handleSubmit2}>

      <h1>Titre</h1>


        <FormGroup>
          <Label for="titrechanson">Titre</Label>
          <Input type="text" name="Titrechanson" id="TitreChanson"  />
        </FormGroup>

        <FormGroup>
          <Label for="duree">Durée</Label>
          <Input
            type="number"
            name="dureechanson"
            id="DureeChanson"

          />
        </FormGroup>
        <FormGroup>
          <Label for="Ecoutes">Ecoutes</Label>
          <Input
            type="number"
            name="ecouteschanson"
            id="EcoutesChanson"

          />
        </FormGroup>
        <FormGroup>
          <Label for="Followers">Mention "j'aime"</Label>
          <Input
            type="number"
            name="nbrjaime"
            id="NbrJaime"

          />
        </FormGroup>
        <FormGroup>
          <Label for="refartistefeat">Featuring</Label>
          <Input type="text" name="Refartistefeat" id="RefArtisteFeat"  />
        </FormGroup>

        <Button className="Submit">Submit</Button>
      </Form>
      </div>

      </div>
      </div>

    );
  }
}
