  render() {
    return (

      <div className="App-header" >
      <div className="row">
      <div className="col-4">
    
     
      <Form onSubmit={this.handleSubmit}>

      <h1>Artiste</h1>

      
        <FormGroup>
          <Label for="nom">Nom</Label>
          <Input id="NomArtiste" onChange={this.handleChange}  />
        </FormGroup>
        <FormGroup>
          <Label for="Date">Birth</Label>
          <Input
            id="DatedeNaissance"
            onChange={this.handleChange1}
            
          />
        </FormGroup>
        <FormGroup>
          <Label for="Followers">Followers</Label>
          <Input
           
            id="nbrfollowers"
            onChange={this.handleChange2}
           
          />
        </FormGroup>
       
        <FormGroup>
          <Label for="Albums">Albums</Label>
          <Input id="albums" onChange={this.handleChange3} />
        </FormGroup>
        <Button className="Submit">Submit</Button>
      </Form>

      </div>

      <div className="col-4">
      <Form onSubmit={this.handleSubmit1}>
      <h1>Album</h1>
      
        <FormGroup>
          <Label for="titre">Titre</Label>
          <Input id="TitreAlbum" onChange={this.handleChange4} />
        </FormGroup>
        <FormGroup>
          <Label for="Date">Date de sortie</Label>
          <Input
            id="DatedeSortie" onChange={this.handleChange5}
          />
        </FormGroup>

        <FormGroup>
          <Label for="genre">Genre</Label>
          <Input id="GenreAlbum"  onChange={this.handleChange6}/>
        </FormGroup>
        <FormGroup>
          <Label for="urlcover">URL Cover</Label>
          <Input id="UrlCoverAlbum" onChange={this.handleChange7}  />
        </FormGroup>
        <FormGroup>
          <Label for="reftitres">Ref Titres</Label>
          <Input id="RefTitresAlbum" onChange={this.handleChange8} />
        </FormGroup>

        <Button className="Submit">Submit</Button>
      </Form>
      </div>


      <div className="col-4">
      <Form onSubmit={this.handleSubmit2}>

      <h1>Titre</h1>


        <FormGroup>
          <Label for="titrechanson">Titre</Label>
          <Input  id="TitreChanson" onChange={this.handleChange9}  />
        </FormGroup>

        <FormGroup>
          <Label for="duree">Durée</Label>
          <Input
        
            id="DureeChanson"
            onChange={this.handleChange10}

          />
        </FormGroup>
        <FormGroup>
          <Label for="Ecoutes">Ecoutes</Label>
          <Input
            id="EcoutesChanson"
            onChange={this.handleChange11}

          />
        </FormGroup>
        <FormGroup>
          <Label for="Followers">Mention "j'aime"</Label>
          <Input
        
            id="NbrJaime"
            onChange={this.handleChange11}

          />
        </FormGroup>
        <FormGroup>
          <Label for="refartistefeat">Featuring</Label>
          <Input id="RefArtisteFeat" onChange={this.handleChange12} />
        </FormGroup>

        <Button className="Submit">Submit</Button>
      </Form>
      </div>

      </div>
      </div>

    );
  }
}
